<script>
  import { exclude } from "utils/exclude";
  let className = "";
  export let theme = "primary";
  export let to = null;
  export let pill = false;
  export let clear = false;
  export { className as class };

  let t = "";
  $: {
    if (clear) {
      t = "bg-tranparent text-blue-500 hover:text-blue-600";
    } else {
      t =
        {
          primary: "bg-blue-500 hover:bg-blue-600 text-white",
          danger: "bg-red-500 hover:bg-red-600 text-white"
        }[theme] + " hover:shadow";
    }
  }

  $: classes = `py-2 px-4 rounded text-center ${t} ${className} `;
</script>

{#if to === null}
  <button
    on:click
    on:mouseover
    class={classes}
    class:rounded-full={pill}
    {...exclude($$props, ['class'])}>
    <slot />
  </button>
{:else}
  <a
    href={to}
    on:click
    on:mouseover
    class={classes}
    class:rounded-full={pill}
    {...exclude($$props, ['class'])}>
    <slot />
  </a>
{/if}
