<script>
  import { exclude } from "utils/exclude";
  let className = "";
  export let theme = "primary";
  export let to = null;
  export let pill = false;
  export { className as class };

  $: t = {
    primary: "bg-blue-500 hover:bg-blue-600 text-white",
    danger: "bg-red-500 hover:bg-red-500 text-white"
  }[theme];

  $: classes = `py-2 px-4 rounded hover:shadow ${t} ${className}`;
</script>

{#if to === null}
  <button
    on:click
    on:mouseover
    class={classes}
    class:rounded-full={pill}
    {...exclude($$props, ['class'])}>
    <slot />
  </button>
{:else}
  <a
    href={to}
    on:click
    on:mouseover
    class={classes}
    class:rounded-full={pill}
    {...exclude($$props, ['class'])}>
    <slot />
  </a>
{/if}
