<script>
  export let col = 12;
  export let sm = null;
  export let md = null;
  export let lg = null;
  export let xl = null;

  let className = "";
  export { className as class };

  /**
   * @param {number} s
   */
  function f(s) {
    if (s == 12) {
      return "full";
    }
    return `${s}/12`;
  }
  function b(b, s) {
    if (s) {
      return b ? `${b}:w-${f(s)}` : `w-${f(s)}`;
    }
    return null;
  }
  // seriously wtf lel
  const classes = [
    b(null, col),
    b("sm", sm),
    b("md", md),
    b("lg", lg),
    b("xl", xl),
    className
  ].join(" ");
</script>

<div class={classes} on:click>
  <slot />
</div>
