<script>
  export let message = "";
  export let theme = "primary";

  const themes = {
    primary: "bg-blue-100 border-blue-500 text-blue-900",
    success: "bg-green-100 border-green-500 text-green-900",
    warning: "bg-orange-100 border-orange-500 text-orange-900",
    danger: "bg-red-100 border-red-500 text-red-900"
  };
  $: t = themes[theme] || themes["primary"];
  $: classes = `border-t-4 cursor-pointer rounded-b px-4 py-3 shadow w-56 transition-shadow duration-100 hover:shadow-xl ${t}`;
</script>

<div class={classes} role="alert">
  {@html message}
</div>
