<script>
  let className;
  export { className as class };
  export let thumbnail = null;
  export let thumbnailHeight = 200;

  $: classes = `
    rounded-lg 
    shadow-lg 
    bg-white 
    ${className}
  `;

  $: thumbnailStyle = `
    background-image: url(${thumbnail}); 
    background-position: center center; 
    background-size: cover; 
    height: ${thumbnailHeight}px; 
    border-top-left-radius: inherit; 
    border-top-right-radius: inherit;
  `;
</script>

<div class={classes}>
  {#if thumbnail}
    <div style={thumbnailStyle} />
  {/if}
  <div class="pt-10 px-10 text-2xl font-bold">
    <slot name="title" />
  </div>
  <div class="px-10 text-md text-gray-700">
    <slot name="subtitle" />
  </div>
  <div class="my-5 px-10">
    <slot name="content" />
  </div>
  <div class="px-10 pb-10 flex justify-end">
    <slot name="actions" />
  </div>
</div>
